const LOADSCRIPT = () => { JSON.parse(($scripts = $("LOAD-SCRIPT")).text()).forEach(e => { $.getScript(e) }), $scripts.remove() }, LOADCSS = () => { JSON.parse(($css = $("LOAD-CSS")).text()).forEach(e => { $.get(e, e => { $("<style>").html(e).appendTo("[data-router]") }) }), $css.remove() }, LOADALL = () => { LOADCSS(), LOADSCRIPT() };